insert into challenge(name, content, criteria)
values ('Auswärts Essen', 'Geniesse ein Abendessen in einem Restaurant. Bestelle alles selbstständig direkt beim Kellner.', 'Hat niemand für dich mit dem Kellner gesprochen?'),
       ('Bahn statt Auto', 'Nutze den ÖV anstatt dein Auto, um deinen nächsten Termin wahrzunehmen.', 'Bist du mit dem ÖV zum Termin gefahren?'),
       ('Feier dich', 'Feier deinen Geburtstag ausswärts (Bar, Minigolf, etc.).', 'Hast du deinen Geburtstag an einem öffentlichen Ort mit anderen Menschen gefeiert?'),
       ('Sport-Schnuppern', 'Besuche eine Schnupperstunde für ein Sportart, die dich Interessiert (z.B Fussball, Karate, etc.).', 'Hast du die ganze Schnupperstunde durchgemacht?'),
       ('Grüsse den Fahrer','Grüsse bei der nächsten Busfahrt den Fahrer.', 'Hast du den Busfahrer begrüsst?'),
       ('Freundschaft unter Nachbarn', 'Lade deinen Nachbarn auf ein Getränk zu dir ein', 'Hast du deinem Nachbarn ein Getränk bei dir angeboten?'),
       ('Raus aus dem Haus', 'Trinke etwas in einem Restaurant/Bar.', 'Hast du deinen Durst in einem Restaurant/Bar gelöscht?'),
       ('Lass dich beraten', 'Lass dich bei deinem nächsten Einkauf vom Ladenpersonal beraten (z.B. beim Schuhe kaufen).', 'Hast du das Gespräch mit dem Personal gesucht?'),
       ('Gemütlicher Abend mit Freunden', 'Lade deine Freunde zu dir nach Hause ein und verbring mit ihnen einen Abend.', 'Hast du den Abend mit Freunden und nicht alleine verbracht?'),
       ('Ruf zurück', 'Wenn du einen Anruf verpasst hast, rufe zurück anstatt zu schreiben.', 'Hast du dich telefonisch zurückgemeldet?'),
       ('Gentleman/Gentlewoman', 'Halte jemandem die Türe auf.', 'Hast du jemandem die Türe aufgehalten?'),
       ('Frag den Nachbarn','Frag deinen Nachbarn ob er dir etwas Ausleihen kann, wenn dir etwas fehlt (z.B. Mehl beim Backen, Rasenmäher, etc.).', 'Hast du deinen Nachbarn gefragt, ob du das benötigte Utensil ausleihen darfst?');


